var page = require('./page');

var Homepage = Object.create(page, {
    /**
     * define elements
     */
    navbar: { get: function () { return browser.element('#header-navigation'); } },
    searchBar: { get: function () { return browser.element('#search-text'); } },
    submitSearch: { get: function () { return browser.element('#search-button'); } },

    header: { get: function () { return browser.element('#app > div > main > header > h1')}},
    /**
     * define or overwrite page methods
     */
    open: { value: function() {
        page.open.call(this);
    } }
});

module.exports = Homepage
