var page = require('./page');

var Homepage = Object.create(page, {
    /**
     * define elements
     */
    resultsCount: { get: function () { return browser.element('#search-results > h3'); } },
    /**
     * define or overwrite page methods
     */
    open: { value: function() {
        page.open.call(this);
    } }
});

module.exports = Homepage
