var Home = require('../page_objects/Homepage.js');
var Results = require('../page_objects/SearchResults.js');

describe('Arby\'s Search Assertion Demo', function () {

    before('Arby\'s Home Page Loads', function () {
      Home.open();
      Home.header.waitForExist();
      Home.header.waitForVisible();
    });

    it('and the header exists', function () {
      expect();
    });



    it('Shows a positive number of results', function(){
      Results.resultsCount.waitForExist();
      Results.resultsCount.waitForVisible();
      var results = Results.resultsCount.getText();
      var integers = results.split(" ").filter(function(int) {
        return int.match(/\d+/g);
      });
      var cleanInteger = integers[0].replace(/,/g, ""); // this makes numbers with commas into integers
      expect(parseInt(cleanInteger)).to.be.above(0);
    });

});
