var Home = require('../page_objects/Homepage.js');

var root = './test/screenshots/Arbys/Home/'
var parentShot = root + '_arbysHomepage.png';

describe('Arby\'s Homepage CSS checks', function () {

    before('On the Arby\'s home page...', function () {
      Home.open();
      Home.searchBar.waitForExist();
      Home.searchBar.waitForVisible();
      browser.saveScreenshot(parentShot);
    });

    it('The Navigation Bar looks correct', function () {
      browser.assertElementLayout(root, parentShot, 'navbar', Home.navbar, 2);
    });

});
